import React, { useState } from "react";



const AuthContext = React.createContext({
  token: "",
  isLoggedIn: false,
  login: (token) => {},
  logout: () => {},
});

// calculate time to know the expiration time

const calculateRemainingTime = (expirationTime) =>{

  // get time in miliseconds using getTime() method of date function

  const currentTime = new Date().getTime();
  const adjExpirationTime = new Date(expirationTime).getTime();
  const remainingDuration = adjExpirationTime - currentTime ;
  return remainingDuration;
}





// define the function and all to handle the data

export const AuthContextProvider = (props) => {
  const initialToken = localStorage.getItem('token');
  const [token, setToken] = useState(initialToken);

  const userIsLoggedIn = !!token; // it is actually a javascriupt syntax to check true or false

  const loginHandler = (token) => {    
    setToken(token);
    // using local storage
    localStorage.setItem('token', token);

  };

  const logoutHandler = () => {
    setToken(null);
    localStorage.removeItem('token');
  };


  // returning the value 
  const contextValue = {
    token: token,
    isLoggedIn: userIsLoggedIn,
    login: loginHandler,
    logout: logoutHandler,
  };

  return (
    // passing the data using value - contextvalue is object name 
    // and value containes the infomation about data

    <AuthContext.Provider value={contextValue}>
      {props.children}
    </AuthContext.Provider>
  );
};

export default AuthContext;
